import React, { useState } from 'react';

const videos = [
  {
    id: 1,
    title: "Inception Trailer",
    url: "https://www.youtube.com/embed/YoHD9XEInc0",
  },
  {
    id: 2,
    title: "Interstellar Trailer",
    url: "https://www.youtube.com/embed/zSWdZVtXT7E",
  },
  {
    id: 3,
    title: "The Batman Trailer",
    url: "https://www.youtube.com/embed/mqqft2x_Aa4",
  },
  {
    id: 4,
    title: "The Dark Knight Rises",
    url: "https://www.youtube.com/embed/g8evyE9TuYk",
  },
];

function App() {
  const [searchQuery, setSearchQuery] = useState("");

  // DSA: Linear search using filter
  const filteredVideos = videos.filter(video =>
    video.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6">
      <h1 className="text-4xl font-bold mb-4 text-center text-purple-400">ðŸŽ¬ Movie Trailers</h1>

      <div className="flex justify-center mb-6">
        <input
          type="text"
          placeholder="Search trailers..."
          className="w-full max-w-md px-4 py-2 rounded-xl border border-gray-600 bg-gray-800 text-white"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredVideos.length > 0 ? (
          filteredVideos.map((video) => (
            <div key={video.id} className="bg-gray-800 rounded-xl overflow-hidden shadow-lg">
              <iframe
                className="w-full aspect-video"
                src={video.url}
                title={video.title}
                frameBorder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
              ></iframe>
              <div className="p-4">
                <h2 className="text-lg font-semibold">{video.title}</h2>
              </div>
            </div>
          ))
        ) : (
          <p className="text-center text-gray-400 col-span-full">No trailers found ðŸ˜¢</p>
        )}
      </div>
    </div>
  );
}

export default App;
